sys_sg_write_back_action_item.execution_script:
(function WriteBackAction(input) {
    //Type appropriate comment here, and begin script below
	var getAsset = new GlideRecord('alm_hardware');
	getAsset.addQuery('asset_tag', input.AssetNumber);
	getAsset.query(); //Execute the query
	if(getAsset.next()){
		gs.addErrorMessage("Asset not added, because it already exists");
	}
	else{
		// does not exist, need to be added. 
		getAsset.asset_tag=input.AssetNumber;
		getAsset.po_number   = input.PO_Number;
		getAsset.assigned  = new GlideDateTime().getDisplayValue();
		getAsset.install_status = 6; // in Stock

		var theID = getAsset.insert();
		if (theID){
			gs.addInfoMessage("Record Added SysID=" + theID);
		}

	}


})(input);
